    f = zeros(3)
    f[1] = sum(Psp) ;
    f[2] = sum(Qsp) ;
    f[3] = sum((1 .- abs.(V)) .^ 2) ;
    h = vcat(delIr[1:6], delIm[1:6]);

    g = zeros(1); 
    return f, g, h
end


